<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-master',
    'version' => 'dev-master',
    'aliases' => 
    array (
    ),
    'reference' => 'f538de786558e53e43d8336c83be3af482a3a360',
    'name' => 'diego/dam_m07_uf03_pac03_garcia-diego',
  ),
  'versions' => 
  array (
    'diego/dam_m07_uf03_pac03_garcia-diego' => 
    array (
      'pretty_version' => 'dev-master',
      'version' => 'dev-master',
      'aliases' => 
      array (
      ),
      'reference' => 'f538de786558e53e43d8336c83be3af482a3a360',
    ),
